import React from "react";

export default function AddCollection(props) {
  let listColors = ["pink", "yellow", "orange", "red", "purple", "blue"];

  let displayVal = props.displayVal;
  return (
    <div
      className="AddCollection w-100 h-100 m-auto position-fixed"
      style={{
        display: `${displayVal ? "flex" : "none"}`,
        top: "0",
        left: "0",
      }}
    >
      <div
        className="addCollection__bg w-100 h-100"
        style={{
          backgroundColor: "#000",
          opacity: "50%",
        }}
      ></div>

      <div
        className="addCollection__content w-100 h-100 m-auto position-absolute"
        style={{
          height: "100vh",
          paddingTop: "8.5rem",
          border: "1px solid #000",
        }}
      >
        <div
          className="content"
          style={{
            minHeight: "fit-content",
            padding: "1.5rem",
            textAlign: "left",
            border: "1px solid #fff",
          }}
        >
          <div className="addCollection__top d-flex w-100">
            <div className="addCollection__caption mr-auto">add collection</div>
            <div className="addCollection__close-btn">
              <ion-icon name="close"></ion-icon>
            </div>
          </div>

          <div className="addCollection__bar bar-name w-100">
            <div className="addCollection__title">name</div>
            <input
              type="text"
              placeholder="My Collection"
              className="w-100 p-4"
            />
          </div>

          <div className="addCollection__bar bar-color w-100">
            <div className="addCollection__title">color</div>
            <div
              className="addCollection__colors w-100 d-flex flex-wrap"
              style={{}}
            >
              {listColors.map(function (color) {
                return (
                  <div
                    className="addCollection__color"
                    style={{
                      width: "2rem",
                      height: "2rem",
                      backgroundColor: "transparent",
                      border: `3px solid ${color}`,
                      cursor: "pointer",
                    }}
                  ></div>
                );
              })}
            </div>
          </div>

          <div className="addCollection__bar bar-custom-color w-100"></div>

          <div
            className="addCollection__buttons w-100"
            style={{
              textAlign: "right",
            }}
          >
            <button
              className="addCollection__button button btn--none-border btn--bg-gray btn--hover-bg-gray-light"
              style={{ backgroundColor: "#3D3C50" }}
            >
              Create
            </button>
            <button className="addCollection__button button btn--none-border btn--hover-bg-gray">
              Cancel
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
