// lib
import { createUseStyles } from "react-jss";
import { useState, useEffect } from "react";
import clsx from "clsx";
import axios from "axios";

// Component
import Collection from "./collection/view/Collection";
import AddCollection from "./collection/actions/addCollection/AddCollection";

import GetCollectionFunc from "./collection/actions/GetCollectionFunc";

const useStyles = createUseStyles({
  title: {
    fontSize: "3rem",
    fontWeight: "bold",
    margin: "2rem 0",
  },
  collections: {
    width: "100%",
    display: "grid",
    gap: "1.7rem",
    transition: "all 0.3s ease",
  },
  actions: {
    gap: "1rem",
  },
  action: {
    padding: "1.2rem 2.4rem !important",
    margin: "auto",
    borderRadius: "1.5rem",
  },
});

export default function Home() {
  const [collections, setCollections] = useState([]);

  useEffect(async () => {
    let auth_id = localStorage.getItem("auth_id");
    if (auth_id) {
      axios
        .get(`api/users/${auth_id}`)
        .then((res) => {
          setUser({
            username: res.data.username,
            id: res.data.id,
          });
        })
        .catch((err) => {
          const errMsg = err.response.data.message;
          console.log(errMsg);
        });
    }
    console.log("collectins");
    const res = await GetCollectionFunc(10, 1);
    const items = res.data.items;
    setCollections(items);
  }, []);

  return (
    <div className="Home">
      <ListCollection collections={collections} />
    </div>
  );
}

const ListCollection = ({ collections }) => {
  const classes = useStyles();
  const [addCollDisplayVal, setAddCollDisplayVal] = useState(false);

  const toggleAddCollDisplayVal = (value) => (event) => {
    setAddCollDisplayVal(value);
  };

  return (
    <div className="ListCollection">
      <div
        className="content"
        style={{
          minWidth: `${collections.length > 0 ? `70rem` : `40rem`}`,
        }}
      >
        <div
          className={clsx(
            classes.topBar,
            "w-100",
            collections.length > 0 ? "d-flex" : "",
            "align-items-center",
            "justify-content-between"
          )}
        >
          <div
            className={clsx(
              classes.title,
              collections.length == 0 ? "w-100" : ""
            )}
          >
            {collections.length > 0
              ? "Collections"
              : "You have no collections."}
          </div>

          <div
            className={clsx(
              classes.actions,
              collections.length > 0 ? "d-flex" : "w-100"
            )}
          >
            <button
              className={clsx(
                classes.action,
                collections.length == 0 ? "d-none" : "",
                "searchCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
              )}
            >
              <ion-icon name="search-outline"></ion-icon>
            </button>

            <button
              className={clsx(
                classes.action,
                "addCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
              )}
              onClick={toggleAddCollDisplayVal(true)}
            >
              <ion-icon name="add-outline"></ion-icon>
              {collections.length > 0 ? "" : "Add Your First Collection"}
            </button>
          </div>
        </div>

        <div
          className={classes.collections}
          style={{
            gridTemplateColumns: `repeat(${
              collections.length > 0 ? "2" : "1"
            },1fr)`,
          }}
        >
          {collections.length > 0 &&
            collections.map((collection) => {
              return <Collection key={collection.id} collection={collection} />;
            })}
        </div>
      </div>

      <AddCollection
        toggleFunc={toggleAddCollDisplayVal}
        displayVal={addCollDisplayVal}
      />
    </div>
  );
};
