// lib
import React from "react";
import { createUseStyles } from "react-jss";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";

// Component
import Header from "../header/Header";
import SignIn from "../users/signIn/SignIn";
import SignUp from "../users/signUp/SignUp";
import Users from "../users/Users";

// =================================================================

const useStyles = createUseStyles({
  ScreenSaver: {
    height: "100vh",
  },
  title: {
    fontSize: "5rem",
  },
  desc: {
    fontSize: "1.6rem",
    color: "#a2c3d9",
  },
  screenSaver__buttons: {
    display: "flex",
    marginTop: "3rem",
  },
  screenSaver__button: {
    textTransform: "capitalize",
    fontWeight: "bold",
  },
  screenSaver__button: {
    "&:not(:last-child)": {
      marginRight: "2rem",
    },
  },
});

function ScreenSaver({ user }) {
  const classes = useStyles();

  return (
    <div className={classes.ScreenSaver}>
      <div className="content">
        <h1 className={classes.title}>Todo App</h1>
        <p className="{classes.de}">
          Keep track of the daily tasks in life and get that satisfaction upon
          copletion.
        </p>
        <div className="screenSaver__buttons btns--flex">
          <Link to="users/signIn">
            <button className="screenSaver__button button btn--none-border btn--bg-pink btn--bb-pink">
              get start
            </button>
          </Link>
          <Link to="">
            <button className="screenSaver__button button btn--none-border btn--bg-gray btn--bb-gray">
              learn more
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
}
export default ScreenSaver;
