import React from "react";

export default function AddCollection(props) {
  let listColors = ["pink", "yellow", "orange", "red", "purple", "blue"];

  let displayVal = props.displayVal;
  return (
    <div
      className="AddCollection m-auto position-fixed"
      style={{
        display: `${displayVal ? "block" : "none"}`,
        top: "0",
        left: "0",
        width: "100vw",
        height: "100vh",
      }}
    >
      <div
        className="addCollection__bg w-100 h-100 position-absolute"
        style={{
          backgroundColor: "#000",
          opacity: "50%",
        }}
      ></div>

      <div
        className="addCollection__container position-absolute"
        style={{
          width: "70%",
          height: "100vh",
          paddingTop: "8.5rem",
          border: "1px solid #000",
        }}
      >
        {/* <div
          className="content"
          style={{
            minHeight: "fit-content",
            padding: "1.5rem",
            textAlign: "left",
            border: "1px solid #fff",
          }}
        > */}
        <div className="addCollection__top d-flex w-100">
          <div
            className="addCollection__caption mr-auto"
            style={{
              fontSize: "2rem",
              textTransform: "capitalize",
              fontWeight: "bold",
            }}
          >
            add collection
          </div>
          <div className="addCollection__close-btn">
            <ion-icon name="close" style={{ fontSize: "2.5rem" }}></ion-icon>
          </div>
        </div>

        <div className="addCollection__bar bar-name w-100">
          <div
            className="addCollection__title"
            style={{ fontSize: "1.6rem", textTransform: "capitalize" }}
          >
            name
          </div>
          <input
            type="text"
            placeholder="My Collection"
            className="w-100 p-4"
          />
        </div>

        <div className="addCollection__bar bar-color w-100">
          <div
            className="addCollection__title"
            style={{ fontSize: "1.6rem", textTransform: "capitalize" }}
          >
            color
          </div>
          <div
            className="addCollection__colors w-100 d-flex flex-wrap"
            style={{}}
          >
            {listColors.map(function (color) {
              return (
                <div
                  className="addCollection__color"
                  style={{
                    width: "2rem",
                    height: "2rem",
                    backgroundColor: "transparent",
                    border: `3px solid ${color}`,
                    cursor: "pointer",
                  }}
                ></div>
              );
            })}
          </div>
        </div>

        <div className="addCollection__bar bar-custom-color w-100">
          <div
            className="addCollection__title"
            style={{ fontSize: "1.6rem", textTransform: "capitalize" }}
          >
            custome color
          </div>
          <div
            className="addCollection__color"
            style={{
              width: "2rem",
              height: "2rem",
              backgroundColor: "pink",
              cursor: "pointer",
            }}
          ></div>
        </div>

        <div
          className="addCollection__buttons w-100 d-flex "
          style={{
            gap: "1rem",
          }}
        >
          <button
            className="addCollection__button button btn--none-border btn--hover-bg-gray-light ml-auto"
            style={{ backgroundColor: "#3D3C50" }}
          >
            Create
          </button>
          <button className="addCollection__button button btn--none-border btn--hover-bg-gray">
            Cancel
          </button>
        </div>
        {/* </div> */}
      </div>
    </div>
  );
}
