// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";

// Component
import ViewCollection from "./viewCollection/ViewCollection";

const useStyles = createUseStyles({
  collection: {
    padding: "2.2rem 2.5rem",
    borderRadius: "2rem",
    gap: "2rem",
    cursor: "pointer",
    backgroundColor: "var(--background-color-header)",
    transition: "all ease 0.3s",
  },
  collection__tag: {
    padding: "1.3rem",
    borderRadius: "1.5rem",
  },
  collection__name: {
    width: "fit-content",
    textAlign: "left",
    fontSize: "2rem",
    textTransform: "capitalize",
    display: "-webkit-box",

    overflow: "hidden",
    textOverflow: "ellipsis",
    wordBreak: "break-word",
  },
  collection__countTasks: {
    color: "#A1A1A6",
    fontSize: "1.6rem",
    width: "fit-content",
  },
});
export default function Collection({ collection }) {
  const classes = useStyles();

  const linkToTask = () => (event) => {
    console.log("fbdfbdfsbdf");
  };

  return (
    <Link to={`/collection/${collection.cId}`}>
      <div
        className={clsx(classes.collection, "d-flex", "align-items-center")}
        key={collection.cId}
        onClick={linkToTask()}
      >
        <div
          className={clsx(classes.collection__tag, "d-flex")}
          style={{
            backgroundColor: `${collection.cColor}`,
          }}
        >
          <ion-icon
            name="bowling-ball-outline"
            style={{
              fontSize: "3.2rem",
            }}
          ></ion-icon>
        </div>
        <div className="collection__details">
          <div
            className={classes.collection__name}
            style={{
              WebkitLineClamp: "1",
              WebkitBoxOrient: "vertical",
            }}
          >
            {collection.cName}
          </div>
          <div className={classes.collection__countTasks}>No tasks</div>
        </div>

        <Routes>
          <Route
            exact
            path="/collection/:type"
            element={<ViewCollection />}
          />
        </Routes>
      </div>
    </Link>
  );
}
