// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import clsx from "clsx";

// Component
import Home from "../../home/Home";
import SignUp from "../signUp/SignUp";
import ResetPass from "../resetPass/ResetPass";

// =================================================================
const useStyles = createUseStyles({
  SignIn: {
    minHeight: "100vh",
  },
  signin__title: {
    fontSize: "5rem",
    marginBottom: "5rem",
  },
  signin__separate: {
    marginBottom: "2rem",
    fontSize: "1.5rem",
    color: "var(--text-color)",
  },
  signin__link: {
    display: "flex",
    alignItems: "center",
    gap: "0.5rem",
    marginTop: "2rem",
    color: "var(--text-color)",
    fontSize: "1.6rem",
  },
  signin__link__light: {
    color: "var(--text-color-light) !important",
  },
  signin__input: {
    fontSize: "1.6rem",
    marginBottom: "2rem",
  },
  signin__error: {
    color: "red",
    textAlign: "left",
    fontSize: "1.3rem",
    marginBottom: "2rem",
  },
});

function isEmptyObject(obj) {
  var name;
  for (name in obj) {
    if (obj.hasOwnProperty(name)) {
      return false;
    }
  }
  return true;
}

function UserForm(HandleLogin) {
  const [details, setDetails] = useState({
    username: "",
    password: "",
  });
  const [errors, setErrors] = useState({});
  const [errorForm, setErrorForm] = useState("");

  const Login = ({ username, password, ...args }) => {
    const myPromise = new Promise((resolve, reject) => {
      let res = axios({
        method: "post",
        url: "https://mvn-task-manager.work/auth/login",
        data: { username, password },
      });

      resolve(res);
    });

    myPromise
      .then((result) => {
        const token = result.data.token;
        const id = result.data.id;
        HandleLogin({ username, id });
        console.log("Logged in successfully");

        setErrorForm("");
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
        setErrorForm(errMsg);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let errorsTemp = validateInfo(details);
    setErrors(errorsTemp);

    if (isEmptyObject(errorsTemp)) {
      Login(details);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDetails({ ...details, [name]: value });
  };

  return { handleChange, details, handleSubmit, errors, errorForm };
}

function validateInfo(values) {
  let errors = {};

  if (!values.username.trim()) {
    errors.username = "Username required";
  } else if (values.username.trim().length < 6) {
    errors.username = "Username to be 6 charactor or more";
  }

  if (!values.password.trim()) {
    errors.password = "Password required";
  } else if (values.password.trim().length < 6) {
    errors.password = "Password to be 6 charactor or more";
  }

  return errors;
}

export default function SignIn({ HandleLogin }) {
  const classes = useStyles();
  const { handleChange, details, handleSubmit, errors, errorForm } =
    UserForm(HandleLogin);

  return (
    <div className={classes.SignIn}>
      <div className="content">
        <h1 className={classes.signin__title}>Sign In.</h1>

        <div className="buttons w-100">
          <button className="button btn--border btn--hover-border btn--full-width btn--flex btn--hover-trans">
            <div className="button__icon">
              <ion-icon name="logo-google"></ion-icon>
            </div>
            <div className="button__text">Continue with Google</div>
          </button>
          <button className="button btn--border btn--hover-border btn--full-width btn--flex btn--hover-trans">
            <div className="button__icon">
              <ion-icon name="logo-facebook"></ion-icon>
            </div>
            <div className="button__text">Continue with Facebook</div>
          </button>
        </div>

        <div className={classes.signin__separate}>or</div>

        <form onSubmit={handleSubmit}>
          <div className="inputs w-100">
            <input
              type="text"
              placeholder="Username"
              className={clsx(
                classes.signup__input,
                "input--border input--focus-border btn--full-width",
                errors.password && "btn--border-error"
              )}
              id="username"
              name="username"
              onChange={handleChange}
              value={details.username}
            />
            {errors.username && (
              <div className={clsx(classes.input__mess)}>
                *{errors.username}
              </div>
            )}
            <input
              type="password"
              placeholder="Password"
              className={clsx(
                classes.signup__input,
                "input--border input--focus-border btn--full-width",
                errors.password && "btn--border-error"
              )}
              id="password"
              name="password"
              onChange={handleChange}
              value={details.password}
            />
            {errors.password && (
              <div className={clsx(classes.input__mess)}>
                *{errors.password}
              </div>
            )}
            <button
              type="submit"
              className="button btn--bg-pink btn--bb-pink btn--none-border w-100"
            >
              <span className="button__text">Sign In</span>
            </button>

            {errorForm !== "" && (
              <div className={classes.signin__error}>*{errorForm}</div>
            )}
          </div>
        </form>

        <div className={classes.signin__link}>
          <span>Don't have an account?</span>
          <Link to="/users/signUp" className={classes.signin__link__light}>
            Sign up
          </Link>
        </div>

        <Link
          to="/users/resetPass"
          className={classes.signin__link + " " + classes.signin__link__light}
        >
          Forgot password?
        </Link>

        <Link to="/" className={classes.signin__link}>
          <ion-icon name="arrow-back-outline"></ion-icon>
          <div>Back to Home</div>
        </Link>
      </div>
    </div>
  );
}
