import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import axios from "axios";
import clsx from "clsx";

import TaskBar from "./TaskBar";
import Task from "./Task";

const useStyles = createUseStyles({
  tasks__tab: {
    border: "1px solid red",
    marginTop: "3rem",
  },
});

export default function ViewTask() {
  const classes = useStyles();
  const [listTasks, setListTasks] = useState([]);
  const [status, setStatus] = useState("all");

  useEffect(() => {
    axios
      .get(`/api/tasks?limit=8&page=1`)
      .then((res) => {
        console.log(res.data);
        setListTasks(res.data.items);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  }, []);

  const handleChangeStatus = (val) => {
    setStatus(val);
  };
  const handleChange = (tasksData) => {
    if (IsEmptyObject(tasksData)) {
      console.log("Khum co");
    } else {
      console.log("set data tasks");
      // setCollection(tasksData);
    }
  };

  return (
    <div className={clsx(classes.tasks__tab)}>
      <TaskBar status={status} count={listTasks.length} />

      <div className="tasks">
        {listTasks.map((task) => {
          return <Task key={task.id} task={task} />;
        })}
      </div>
    </div>
  );
}
