import { createUseStyles } from "react-jss";
import { useState, useEffect } from "react";
import clsx from "clsx";
import axios from "axios";

import SearchTaskFunc from "./SearchTaskFunc";

const useStyles = createUseStyles({
  searchTasks__bar: {
    display: "flex",
    top: "150%",
    right: "0",
    width: "30rem",
    paddingRight: "1.3rem",
    borderRadius: "1.3rem",
    border: "3px solid var(--background-color-header)",
    position: "absolute",
    alignItems: "center",
  },
  searchTasks__button: {
    padding: "0.3rem",
    borderRadius: "0.7rem",
    display: "flex",
  },
});

export default function SearchTask({ handleChange }) {
  const classes = useStyles();

  const [searchVal, setSearchVal] = useState("");

  const searchTasks = () => {
    axios
      .get(`api/tasks?limit=10&page=1&search=${searchVal}`)
      .then((result) => {
        handleChange(result.data);
      })
      .catch((error) => {
        const errorMsg = error.response.data.message;
        console.log(errorMsg);
      });
  };

  return (
    <div className={clsx(classes.searchTasks__bar)}>
      <div className="searchTasks__input w-100">
        <input
          type="text"
          placeholder="Input tasks name"
          className="w-100 input--none-border"
          style={{
            fontSize: "1.6rem",
          }}
          value={searchVal}
          onChange={(e) => setSearchVal(e.target.value)}
        />
      </div>

      <div className="searchTasks__button" onClick={searchTasks}>
        <ion-icon
          name="search"
          style={{
            fontSize: "2rem",
            cursor: "pointer",
          }}
        ></ion-icon>
      </div>
    </div>
  );
}
