// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";

import Header from "../header/Header";
import ListCollection from "./collection/Collection";
import ViewCollection from "./collection/viewCollection/ViewCollection";
import Collection from "./collection/Collection";

const useStyles = createUseStyles({
  title: {
    fontSize: "3rem",
    fontWeight: "bold",
    margin: "2rem 0",
  },
  collections: {
    width: "100%",
    display: "grid",
    gap: "1.7rem",
    transition: "all 0.3s ease",
  },
  action: {
    padding: "1.2rem 2.4rem",
                  margin: "auto",
                  borderRadius: "1.5rem",
  },
});
export default function Home() {
  const classes = useStyles();
  let collections = [
    {
      cId: 1,
      cName:
        "lorem ipsum dolor sit amet, consectetur adipsegf lorem ipsum dolor sit amet, consectetur adip",
      cColor: "yellow",
    },
    { cId: 2, cName: "abc", cColor: "red" },
    { cId: 3, cName: "abc", cColor: "pink" },
    { cId: 4, cName: "abc", cColor: "coral" },
    { cId: 5, cName: "abc", cColor: "green" },
  ];

  return (
    <div className="Home">
      <div
        className="content"
        style={{
          minWidth: `${collections.length > 0 ? `70rem` : `40rem`}`,
        }}
      >
        <div
          className={clsx(
            classes.topBar,
            "w-100",
            collections.length > 0 ? "d-flex" : "",
            "align-items-center",
            "justify-content-between"
          )}
        >
          <div
            className={clsx(
              classes.title,
              collections.length == 0 ? "w-100" : ""
            )}
          >
            {collections.length > 0
              ? "Collections"
              : "You have no collections."}
          </div>

          <div
            className={clsx(
              classes.actions,
              collections.length > 0 ? "d-flex" : "w-100",
            )}
          >
            <div
              className={clsx(
                classes.action,
                collections.length == 0 ? "d-none" : ""
              )}
            >
              <button
                className="addCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
                style={{
                  margin: "auto 0",
                  borderRadius: "1.5rem",
                }}
              >
                <ion-icon name="search-outline"></ion-icon>
              </button>
            </div>
            <div className={clsx(classes.action)}>
              <button
                className="addCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
               
              >
                <ion-icon name="add-outline"></ion-icon>
                {collections.length > 0 ? "" : "Add Your First Collection"}
              </button>
            </div>
          </div>
        </div>

        <div
          className={classes.collections}
          style={{
            gridTemplateColumns: `repeat(${
              collections.length > 0 ? "2" : "1"
            },1fr)`,
          }}
        >
          {collections &&
            collections.length > 0 &&
            collections.map((collection) => {
              return <Collection collection={collection} />;
            })}
        </div>
      </div>
    </div>
  );
}
