// lib
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

// components
// import SignIn from "../users/signIn/SignIn";

// service
import ToggleUserBar from "../../service/header/ToggleUserBar";
import ToggleSettingBar from "../../service/header/ToggleSettingBar";

// =================================================================
const useStyles = createUseStyles({
  users__avt: {
    width: "5rem",
    height: "5rem",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundImage:
      "url(" +
      "https://www.kindpng.com/picc/m/78-786207_user-avatar-png-user-avatar-icon-png-transparent.png" +
      ")",
    cursor: "pointer",
  },

  usersBar__hidden: {
    top: "105%",
    right: "4rem",
    minWidth: "25rem",
    fontSize: "1.4rem",
    border: "radius 0.7rem",
    color: "var(--text-color-light)",
    backgroundColor: "var(--background-color-header)",

    "& > *": {
      padding: "1.5rem",
    },
  },

  settingBar: {
    cursor: "pointer",
  },
});

const HeaderUser = ({ HandleLogout, user }) => {
  const classes = useStyles();

  const { toggleUserBarFunc } = ToggleUserBar();
  const { toggleSettingBarFunc } = ToggleSettingBar();

  const [toggleUserBarValue, setToggleUserBarValue] = useState();
  const [toggleSettingBarValue, setToggleSettingBarValue] = useState();

  const handleToggleUserBar = (value) => {
    setToggleUserBar(value);
  };
  const handleToggleSettingBar = (value) => {
    setToggleSettingBar(value);
  };

  return (
    <div className="usersBar">
      <div className="usersBar__display">
        <div
          className={clsx(classes.users__avt, "rounded-circle")}
          id="users__avt"
          onClick={toggleUserBarFunc(handleToggleUserBar)}
        ></div>
      </div>

      <div
        className={clsx(
          classes.usersBar__hidden,
          "position-absolute",
          !toggleUserBarValue && "d-none"
        )}
        id="usersBar__hidden"
      >
        <div className="usersBar__review">
          <div className="usersBar__title">Signed in as</div>
          <div className="usersBar__fullname font-weight-bold text-capitalize">
            {user.username}
          </div>
        </div>

        <div className={classes.settingBar}>
          <div className="settingBar__display" onClick={toggleSettingBarFunc()}>
            Account Settings
          </div>
          <div
            className={clsx(
              classes.settingBar__hidden,
              !toggleSettingBarValue && "d-none"
            )}
          ></div>
        </div>

        <button
          className="signOutBtn button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left "
          style={{ padding: "1.5rem" }}
          onClick={HandleLogout}
        >
          Sign out
        </button>
      </div>
    </div>
  );
};

export default HeaderUser;
