import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

const useStyles = createUseStyles({
  task__bar: {
    borderRadius: "1.3rem",
    padding: "1rem",
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
    width: "100%",
    position: "relative",
  },
  task__btns: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    position: "relative",
  },
  collection__actions: {
    minWidth: "max-content",
    top: "100%",
    right: "5%",
    borderRadius: "1.1rem",
    backgroundColor: "#1e1e29",
    zIndex: "2",
    border: "3px solid var(--background-color-header)",
    position: "absolute",
  },
  collection__action: {
    fontSize: "1.3rem",
    padding: "1.2rem 1.8rem",
  },
  task__title: { width: "100%", fontSize: "1.6rem", flexGrow: "1" },
  task__top: {
    width: "100%",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  task__checkbox: {
    border: "1px solid #fff",
    width: "2rem",
    height: "2rem",
    position: "relative",
    borderRadius: "0.2rem"
  },
  task__input: {
    position: "absolute",
    width: "100%",
    height: "100%",
    opacity: "0",
  },
  task__selectIcon: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    height: "100%",
    backgroundColor: "var(--bg-btn-pink)"
  },
});

export default function Task({ task, handleChangeData }) {
  const classes = useStyles();
  // console.log(task);

  const [displayActionsBarVal, setDisplayActionsBarVal] = useState(false);

  const toggelActionsBar = (e) => {
    setDisplayActionsBarVal(!displayActionsBarVal);
  };

  const [checkboxSelected, setCheckboxSelected] = useState(false);
  const handleSelectionChange = (e) => {
    console.log(e.target.checked);
    setCheckboxSelected(e.target.checked);
  };

  return (
    <div
      className={clsx(classes.task__bar)}
      style={{
        border:
          task.status === "IN_PROGRESS"
            ? `3px solid var(--background-color-header)`
            : "3px solid red",
        backgroundColor: "var(--background-color-header)",
      }}
    >
      <div className={clsx(classes.task__btns)}>
        <div className={clsx(classes.task__top)}>
          <div className={clsx(classes.task__checkbox)}>
            <div className={clsx(classes.task__selectIcon)}>
              <ion-icon name="checkmark"></ion-icon>
            </div>
            <input
              type="checkbox"
              className={clsx(classes.task__input)}
              onChange={handleSelectionChange}
            />
          </div>
          <ion-icon
            name="ellipsis-vertical"
            style={{ fontSize: "2rem", marginLeft: "auto" }}
            onClick={toggelActionsBar}
          ></ion-icon>
        </div>

        <div
          className={clsx(
            classes.collection__actions,
            !displayActionsBarVal && "d-none"
          )}
        >
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            edit task
          </div>
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            Submit Task
          </div>
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            delete task
          </div>
        </div>
      </div>

      <div className={clsx(classes.task__title)}>{task.title}</div>
    </div>
  );
}
