// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";

import Header from "../header/Header";
import ListCollection from "./collection/Collection";
import ViewCollection from "./collection/viewCollection/ViewCollection";
import AddCollection from "./collection/addCollection/AddCollection";
import Collection from "./collection/Collection";

const useStyles = createUseStyles({
  title: {
    fontSize: "3rem",
    fontWeight: "bold",
    margin: "2rem 0",
  },
  collections: {
    width: "100%",
    display: "grid",
    gap: "1.7rem",
    transition: "all 0.3s ease",
  },
  actions: {
    gap: "1rem",
  },
  action: {
    padding: "1.2rem 2.4rem !important",
    margin: "auto",
    borderRadius: "1.5rem",
  },
  // //////////////////////////

  AddCollection: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  addCollection__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  addCollection__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  addCollection__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  addCollection__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  addCollection__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  addCollection__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 1s ease",
  },
  addCollection__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  addCollection__bar: {
    marginBottom: "2rem",
    width: "100%",
  },
});

const useStyles2 = createUseStyles({
  collection: {
    padding: "2.2rem 2.5rem",
    borderRadius: "2rem",
    gap: "2rem",
    cursor: "pointer",
    backgroundColor: "var(--background-color-header)",
    transition: "all ease 0.3s",
  },
  collection__tag: {
    padding: "1.3rem",
    borderRadius: "1.5rem",
  },
  collection__name: {
    width: "fit-content",
    textAlign: "left",
    fontSize: "2rem",
    textTransform: "capitalize",
    display: "-webkit-box",

    overflow: "hidden",
    textOverflow: "ellipsis",
    wordBreak: "break-word",
  },
  collection__countTasks: {
    color: "#A1A1A6",
    fontSize: "1.6rem",
    width: "fit-content",
  },
});

export default function Home() {
  const classes = useStyles();

  let collections = [
    {
      cId: 1,
      cName:
        "lorem ipsum dolor sit amet, consectetur adipsegf lorem ipsum dolor sit amet, consectetur adip",
      cColor: "yellow",
    },
    { cId: 2, cName: "abc", cColor: "red" },
    { cId: 3, cName: "abc", cColor: "pink" },
    { cId: 4, cName: "abc", cColor: "coral" },
    { cId: 5, cName: "abc", cColor: "green" },
  ];

  const [displayVal, setDisplayVal] = useState(false);

  const hideAddCollectionBar = () => {
    setDisplayVal(false);
  };
  const showAddCollectionBar = () => {
    setDisplayVal(true);
  };

  const ListCollection = () => {
    const [collectionColor, setCollectionColor] = useState("");

    function setBgColor(color) {
      console.log(color);
      setCollectionColor(color)
    };

    return (
      <div className="ListCollection">
        <div
          className="content"
          style={{
            minWidth: `${collections.length > 0 ? `70rem` : `40rem`}`,
          }}
        >
          <div
            className={clsx(
              classes.topBar,
              "w-100",
              collections.length > 0 ? "d-flex" : "",
              "align-items-center",
              "justify-content-between"
            )}
          >
            <div
              className={clsx(
                classes.title,
                collections.length == 0 ? "w-100" : ""
              )}
            >
              {collections.length > 0
                ? "Collections"
                : "You have no collections."}
            </div>

            <div
              className={clsx(
                classes.actions,
                collections.length > 0 ? "d-flex" : "w-100"
              )}
            >
              <button
                className={clsx(
                  classes.action,
                  collections.length == 0 ? "d-none" : "",
                  "searchCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
                )}
              >
                <ion-icon name="search-outline"></ion-icon>
              </button>

              <button
                className={clsx(
                  classes.action,
                  "addCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
                )}
                onClick={showAddCollectionBar}
              >
                <ion-icon name="add-outline"></ion-icon>
                {collections.length > 0 ? "" : "Add Your First Collection"}
              </button>
            </div>
          </div>

          <div
            className={classes.collections}
            style={{
              gridTemplateColumns: `repeat(${
                collections.length > 0 ? "2" : "1"
              },1fr)`,
            }}
          >
            {collections &&
              collections.length > 0 &&
              collections.map((collection) => {
                return (
                  <Collection key={collection.cId} collection={collection} />
                );
              })}
          </div>
        </div>

        <AddCollection />
      </div>
    );
  };

  const AddCollection = () => {
    const classes = useStyles();
    let listColors = ["pink", "yellow", "orange", "red", "purple", "blue"];
    // let [displayVal, setDisplayVal] = useState(displayVal);

    return (
      <div
        className={clsx(
          classes.AddCollection,
          "m-auto position-fixed",
          !displayVal && "d-none"
        )}
      >
        <div
          className={clsx(
            classes.addCollection__bg,
            "w-100 h-100 position-absolute"
          )}
          onClick={hideAddCollectionBar}
        ></div>

        <div
          className={clsx(
            classes.addCollection__container,
            "position-absolute"
          )}
        >
          <div className="addCollection__top d-flex w-100">
            <div className={clsx(classes.addCollection__caption, "mr-auto")}>
              add collection
            </div>
            <div
              className="addCollection__close-btn"
              onClick={hideAddCollectionBar}
            >
              <ion-icon name="close"></ion-icon>
            </div>
          </div>

          <div className={clsx(classes.addCollection__bar, "bar-name")}>
            <div className={clsx(classes.addCollection__title)}>name</div>
            <input
              type="text"
              placeholder="My Collection"
              className="btn--outline w-100 py-3"
              style={{ fontSize: "1.6rem" }}
            />
          </div>

          <div className={clsx(classes.addCollection__bar, "bar-color")}>
            <div className={clsx(classes.addCollection__title)}>color</div>
            <div className={clsx(classes.addCollection__colors, "w-100")}>
              {listColors.map(function (color) {
                return (
                  <div
                    className={clsx(classes.addCollection__color)}
                    key={`${color}`}
                    style={{
                      border: `3px solid ${color}`,
                    }}
                  ></div>
                );
              })}
            </div>
          </div>

          <div className={clsx(classes.addCollection__bar, "bar-custom-color")}>
            <div className={clsx(classes.addCollection__title)}>
              custome color
            </div>
            <div
              className={clsx(classes.addCollection__color)}
              style={{
                backgroundColor: "#FC76A1",
              }}
            ></div>
          </div>

          <div className={clsx(classes.addCollection__buttons, "w-100 d-flex")}>
            <button
              className="addCollection__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
              style={{ backgroundColor: "#3D3C50" }}
            >
              Create
            </button>
            <button className="addCollection__button button btn--none-border btn--hover-bg-gray mb-0">
              Cancel
            </button>
          </div>
        </div>
      </div>
    );
  };

  function Collection({ collection }) {
    const classes = useStyles2();
  
    return (
      <div
        className={clsx(classes.collection, "d-flex", "align-items-center")}
        key={collection.cId}
      >
        <div
          className={clsx(classes.collection__tag, "d-flex")}
          style={{
            backgroundColor: `${collection.cColor}`,
          }}
        >
          <ion-icon
            name="bowling-ball-outline"
            style={{
              fontSize: "3.2rem",
            }}
          ></ion-icon>
        </div>
        <div className="collection__details">
          <div
            className={classes.collection__name}
            style={{
              WebkitLineClamp: "1",
              WebkitBoxOrient: "vertical",
            }}
          >
            {collection.cName}
          </div>
          <div className={classes.collection__countTasks}>No tasks</div>
        </div>
      </div>
    );
  }
  return (
    <div className="Home">
      <ListCollection />
    </div>
  );
}
