// lib
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const useStyles = createUseStyles({
  Settings: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  settings__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  settings__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  settings__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  settings__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  settings__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  settings__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 0.1s ease-in-out",
    position: "relative",
  },
  settings__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  settings__group: {
    marginBottom: "2rem",
    width: "100%",
  },
  settings__err: {
    color: "red",
    padding: "1rem",
    fontSize: "1.4rem",
  },
  settings__listColls: {
    height: "16rem",
    overflowY: "scroll",
    borderRadius: "1.2rem",
    border: "3px solid var(--background-color-header)",
  },
  settings__itemColl: {
    display: "flex",
    alignItems: "center",
    padding: "1rem",
    borderBottom: "2px solid var(--background-color-header)",
    gap: "1rem",
    cursor: "pointer",
  },
  settings__checkbox: {
    color: "var(--bg-btn-pink)",
    backgroundColor: "transparent",
    width: "1.7rem",
    height: "1.7rem",
  },
  settings__lable: {
    fontSize: "1.6rem",
  },
});

const Settings = ({
  displayVal,
  toggleFunc,
}) => {
  const classes = useStyles();

  const HandleEditTask = (values) => {
    axios({
      method: "patch",
      url: "api/tasks/${task.id}",
      data: { title, categoryIds, status },
    })
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        toast.error(errMsg);
      });
  };

  const { handleChange, handleSubmit, values, errors } =
    useTaskForm(HandleEditTask);

  const closeTabFunc = () => (event) => {
    toggleFunc(false)();
  };

  const handleChangeOption = (id) => (event) => {
    let checked = event.target.checked;
    console.log(listSelections);
    let existing = listSelections.includes(id);
    console.log(existing)

    if (existing && !checked) {
      const newArr = listSelections.filter((select) => select !== id);
      setListSelections(newArr);
    } else if (!existing && checked) {
      setListSelections([...listSelections, id]);
    }
  };

  return (
    <div
      className={clsx(
        classes.Settings,
        "m-auto position-fixed",
        !displayVal && "d-none"
      )}
    >
      <div
        className={clsx(classes.settings__bg, "w-100 h-100 position-absolute")}
        onClick={closeTabFunc(false)}
      ></div>

      <div className={clsx(classes.settings__container, "position-absolute")}>
        <form method="post" onSubmit={handleSubmit}>
          <div className="settings__top d-flex w-100">
            <div className={clsx(classes.settings__caption, "mr-auto")}>
              Edit Task
            </div>
            <div className="settings__close-btn" onClick={closeTabFunc(false)}>
              <ion-icon name="close"></ion-icon>
            </div>
          </div>

          <div className={clsx(classes.settings__group, "bar-title")}>
            <div className={clsx(classes.settings__title)}>Title</div>
            <input
              type="text"
              placeholder="My Task"
              className="btn--outline w-100 py-3"
              style={{ fontSize: "1.6rem" }}
              name="title"
              value={values.title}
              onChange={handleChange}
            />
            {errors.title && (
              <div className={clsx(classes.settings__err)}>*{errors.title}</div>
            )}
          </div>

          <div className={clsx(classes.settings__group, "bar-listColl")}>
            <div className={clsx(classes.settings__title)}>List Collection</div>
            <div className={clsx(classes.settings__listColls)}>
              {listCollections.map((item) => {
                return (
                  <div
                    key={item.id}
                    className={clsx(classes.settings__itemColl)}
                  >
                    <input
                      type="checkbox"
                      className={clsx(classes.settings__checkbox)}
                      checked={task.categories.find(c => c.id === item.id)}
                      onChange={handleChangeOption(item.id)}
                    />
                    <span className={clsx(classes.settings__lable)}>
                      {item.name}
                    </span>
                  </div>
                );
              })}
            </div>
          </div>

          <div className={clsx(classes.settings__buttons, "w-100 d-flex")}>
            <button
              className="settings__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
              style={{ backgroundColor: "#3D3C50" }}
              type="submit"
            >
              Submit
            </button>
            <button
              type="reset"
              className="settings__button button btn--none-border btn--hover-bg-gray mb-0"
              onClick={closeTabFunc()}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Settings;
