import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";



const useStyles = createUseStyles({
  tasks__tab: {
    marginTop: "3rem",
    width: "80%",
    maxWidth: "100rem",
  },
  tasks: {
    display: "grid",
    gap: "1.7rem",
    gridTemplateColumns: "repeat(auto-fit, minmax(20rem, 1fr))",
  },
});

export default function Task({ task, handleChangeData }) {
  console.log(task);

  const [displayActionsBarVal, setDisplayActionsBarVal] = useState(false);

  const toggelActionsBar = (e) => {};

  return (
    <div
      className="task__bar w-100 position-relative"
      style={{
        borderRadius: "1.3rem",
        border:
          task.status === "IN_PROGRESS"
            ? `3px solid var(--background-color-header)`
            : "3px solid red",
        padding: "1rem",
        display: "flex",
        flexDirection: "column",
        gap: "1rem",
      }}
    >
      <div
        className="task__btns"
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          position: "relative",
        }}
      >
        <ion-icon
          name="ellipsis-vertical"
          style={{
            fontSize: "2rem",
            marginLeft: "auto",
          }}
          onClick={toggelActionsBar}
        ></ion-icon>
        <div
          className="collection__actions position-absolute"
          style={{
            minWidth: "max-content",
            top: "100%",
            right: "5%",
            borderRadius: "1.1rem",
            backgroundColor: "#1e1e29",
            zIndex: "2",
            border: "3px solid var(--background-color-header)",
          }}
        >
          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              fontSize: "1.3rem",
              padding: "1.2rem 1.8rem",
            }}
          >
            edit task
          </div>
          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              fontSize: "1.3rem",
              padding: "1.2rem 1.8rem",
            }}
          >
            Submit Task
          </div>

          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              fontSize: "1.3rem",
              padding: "1.2rem 1.8rem",
            }}
          >
            delete task
          </div>
        </div>
      </div>

      <div
        className="task__title w-100"
        style={{
          fontSize: "1.6rem",
          flexGrow: "1",
        }}
      >
        {task.title}
      </div>

      <div
        className="task__process w-100 position-absolute"
        style={{
          top: "100.5%",
          left: "0",
        }}
      >
        <div
          className="task__process-bar"
          style={{
            width: "0%",
            height: "2.5px",
            //   backgroundColor: `${collection.cColor}`,
          }}
        ></div>
      </div>
    </div>
  );
}
