// lib
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import axios from "axios";
import clsx from "clsx";

// Component
import CollectionBar from "./CollectionBar";
import InProcessTasks from "../../task/view/InProcessTasks";
import CompletedTasks from "../../task/view/CompleteTasks";

// Service
import IsEmptyObject from "../../../../service/IsEmptyObject";

const useStyles = createUseStyles({
  content: {
    textAlign: "left",
    maxWidth: "unset",
  },
  tasks__tab: {
    marginTop: "1rem",
    width: "80%",
  },
});

export default function ViewCollection() {
  const classes = useStyles();
  let { cId } = useParams();

  const [collection, setCollection] = useState({});
  const [inProcessTasks, setInProcessTasks] = useState([]);
  const [completeTasks, setCompleteTasks] = useState([]);

  function getCollection() {
    axios
      .get(`api/categories/${cId}`)
      .then((res) => {
        setCollection(res.data);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  }
  function getTasks() {
    axios
      .get(`api/tasks?page=1`)
      .then((res) => {
        console.log(res.data)
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  }

  useEffect(() => {
    if (cId) {
      Promise.all([getCollection(), getTasks()])
      .then(function (
        results
      ) {
        const a = results[0];
        const b = results[1];
      });
    }
  }, []);

  const handleChange = (tasksData) => {
    // setCollection(tasksData);

    if (IsEmptyObject(tasksData)) {
      console.log("Khum co");
    }
  };

  return (
    <div className="ViewCollection">
      <div className={clsx(classes.content, "content")}>
        <CollectionBar collection={collection} handleChange={handleChange} />

        <div className={clsx(classes.tasks__tab)}>
          <InProcessTasks collection={collection} />
        </div>
      </div>
    </div>
  );
}
