// lib
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

// Component
import TaskBar from "./TaskBar";
import Task from "./Task";
import Paging from "./Paging";

// services
import GetTasksFunc from "../../task/actions/GetTasksFunc";
import GetCollectionFunc from "../../collection/actions/GetCollectionFunc";

// Service
const useStyles = createUseStyles({
  content: {
    textAlign: "left",
    maxWidth: "unset",
  },
  tasks: {
    display: "grid",
    gap: "1.7rem",
    gridTemplateColumns: "repeat(auto-fit, minmax(20rem, 1fr))",
  },
});

export default function ListTasks() {
  const classes = useStyles();

  const [data, setData] = useState({});
  const [listCollections, setListCollections] = useState([]);

  //   get Tasks
  useEffect(async () => {
    const tasksData = await GetTasksFunc(8);
    handleChangeData(tasksData.data);
  }, []);
  //   get Collections
  useEffect(async () => {
    const res = await GetCollectionFunc(10);
    setListCollections(res.data.items);
  }, []);

  const [selectedTasks, setSelectedTasks] = useState([]);
  const handleSelectTasks = (id) => (event) => {
    setSelectedTasks([...selectedTasks, id]);

    console.log(selectedTasks);
  };
  const handleChangeData = (data) => {
    console.log(data);
    setData(data);
  };

  return (
    <div className="ViewCollection">
      <div className={clsx(classes.content, "content")}>
        <TaskBar
          listCollections={listCollections}
          count={data && data.meta ? data.meta.totalItems : 0}
          handleChangeData={handleChangeData}
        />

        <div className={clsx(classes.tasks)}>
          {data &&
            data.items &&
            data.items.map((task) => {
              return (
                <Task
                  key={task.id}
                  listCollections={listCollections}
                  task={task}
                  handleChangeData={handleChangeData}
                  handleSelectTasks={handleSelectTasks}
                />
              );
            })}
        </div>

        {data && data.meta && data.meta.totalItems > 0 && (
          <Paging data={data} handleChangeData={handleChangeData} />
        )}
      </div>
    </div>
  );
}
