import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import axios from "axios";
import { toast } from "react-toastify";

import GetTasksFunc from "../GetTasksFunc";

const useStyles = createUseStyles({
  addTasks__bar: {
    display: "flex",
    top: "150%",
    right: "0",
    width: "30rem",
    padding: "0 1.3rem",
    borderRadius: "1.3rem",
    border: "3px solid var(--bg-btn-pink)",
    position: "absolute",
    alignItems: "center",
  },
  addTasks__button: {
    padding: "0.3rem",
    borderRadius: "0.7rem",
    display: "flex",
  },
});

export default function AddTask({ collection, handleChangeData }) {
  const classes = useStyles();

  const [title, setTitle] = useState("");
  const categoryIds = [collection.id];

  const createTask = async () => {
    axios({
      method: "post",
      url: "api/tasks",
      data: { title, categoryIds },
    })
      .then(async (res) => {
        toast.success("ðŸ¦„ Create task successfully!");
        setTitle("");

        const tasksData = await GetTasksFunc(4);
        handleChangeData(tasksData.data);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  };

  return (
    <div className={clsx(classes.addTasks__bar)}>
      <div className={clsx(classes.addTasks__button)} onClick={createTask}>
        <ion-icon name="add"></ion-icon>
      </div>
      <div className="addTasks__input w-100">
        <input
          type="text"
          placeholder="Add task"
          className="w-100 input--none-border"
          
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div className="addTasks__button" onClick={() => setTitle("")}>
        <ion-icon
          name="close"
          style={{
            fontSize: "2rem",
          }}
        ></ion-icon>
      </div>
    </div>
  );
}
