// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";
import axios from "axios";

// Service
import NavigateLink from "../../../../service/NavigateLink";

function CollectionBar(props) {
  const collection = props.data;

  return (
    <div className="collection__bar d-flex align-items-center w-100">
      <div
        className="collection__back-btn"
        style={{
          padding: "1.2rem",
          marginRight: "1.5rem",
          borderRadius: "1.5rem",
          backgroundColor: "var(--background-color-header)",
          cursor: "pointer",
        }}
        onClick={NavigateLink("")}
      >
        <ion-icon
          name="chevron-back-outline"
          style={{
            fontSize: "2.7rem",
          }}
        ></ion-icon>
      </div>
      <div
        className="collection__name mr-auto"
        style={{
          fontSize: "3rem",
          textTransform: "capitalize",
        }}
      >
        {collection.cName}
      </div>

      <div
        className="taskActions d-flex align-items-center"
        style={{
          gap: "1rem",
          marginRight: "1rem",
        }}
      >
        <div className="taskAction position-relative">
          <div className="taskAction__icon">
            <ion-icon name="add"></ion-icon>
          </div>
          {<AddTask collection={collection} />}
        </div>

        <div className="taskAction position-relative">
          <div className="taskAction__icon">
            <ion-icon name="search"></ion-icon>
          </div>
          {<SearchTasks collection={collection} />}
        </div>
      </div>

      <div
        className="collection__dots position-relative"
        style={{
          cursor: "pointer",
        }}
      >
        <ion-icon
          name="ellipsis-horizontal"
          style={{
            fontSize: "2.5rem",
          }}
        ></ion-icon>
        <div
          className="collection__actions position-absolute d-none"
          style={{
            minWidth: "max-content",
            right: "0",
            borderRadius: "1.1rem",
            backgroundColor: "#1e1e29",
            zIndex: "2",
          }}
        >
          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              width: "20rem",
            }}
          >
            edit collection
          </div>
          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              width: "20rem",
            }}
          >
            delete collection
          </div>
        </div>
      </div>
    </div>
  );
}


export default function ViewCollection() {
  let { cId } = useParams();

  const [collection, setCollection] = useState({});
  useEffect(() => {
    if (cId) {
      axios
        .get(`api/categories/${cId}`)
        .then((res) => {
          setCollection(res.data);
          console.log(collection.name);
        })
        .catch((err) => {
          const errMsg = err.response.data.message;
          console.log(errMsg);
        });
    }
  }, []);

  console.log("object");
  return (
    <div className="ViewCollection">
      <div
        className="content d-flex flex-column"
        style={{
          textAlign: "left",
          maxWidth: "70rem",
          minWidth: "70rem",
        }}
      >
        <div className="tasks__tab d-flex justify-content-between w-100 mt-5"></div>
      </div>
    </div>
  );
}
