import React from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import taskAPI from "../../service/fetchAPI/taskAPI";
import { useState, useEffect } from "react";

const useStyles = createUseStyles({
  paging__list: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "0.7rem",
    border: "3px solid var(--background-color-header)",
  },
  paging__item: {
    width: "4.2rem",
    height: "4rem",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "1.5rem",
    borderRadius: "1rem",
    outline: "none !important",
    border: "none",
    color: "var(--bg-btn-pink)",
    backgroundColor: "var(--background-color-header)",
  },
  paging__chevron: {
    margin: "0 1rem",
    color: "var(--bg-btn-pink)",
  },
  paging__input: {},
  paging__item_disabled: {
    color: "#999",
    cursor: "default !important",
  },
  paging__icon_disabled: {
    cursor: "default !important",
  },
});

export default function Paging({ data, handleChangeData }) {
  const classes = useStyles();

  let [currentPage, setCurrentPage] = useState(1);
  const handleChangePaging = (link) => async (event) => {
    handleChangeData(
      await taskAPI().getTasks(link.substring(link.lastIndexOf("/api")))
    );
  };

  return (
    <div className={clsx(classes.paging__list)}>
      <button
        className={clsx(
          classes.paging__item,
          classes.paging__chevron,
          data.links.previous == null && classes.paging__item_disabled
        )}
        onClick={handleChangePaging(data.links.previous)}
        disabled={data.links.previous == null && true}
      >
        <ion-icon
          name="chevron-back"
          className={clsx(classes.paging__icon_disabled)}
        ></ion-icon>
      </button>

      <button
        className={clsx(classes.paging__item)}
        onClick={handleChangePaging(data.links.first)}
      >
        1
      </button>
      <input
        type="number"
        className={classes.paging__input}
        value={
          data.meta &&
          data.meta.currentPage > 1 &&
          data.meta.currentPage < data.meta.totalPages
            ? data.meta.currentPage
            : ""
        }
      />
      <button
        className={clsx(classes.paging__item)}
        onClick={handleChangePaging(data.links.last)}
      >
        {data.meta ? data.meta.totalPages : ""}
      </button>

      <button
        className={clsx(
          classes.paging__item,
          classes.paging__chevron,
          data.links.next == null && classes.paging__item_disabled
        )}
        onClick={handleChangePaging(data.links.next)}
        disabled={data.links.next == null && true}
      >
        <ion-icon name="chevron-forward"></ion-icon>
      </button>
    </div>
  );
}
