// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";

// Component
import { ChromePicker } from "react-color";
import Collection from "./collection/Collection";
import axios from "axios";

const useStyles = createUseStyles({
  title: {
    fontSize: "3rem",
    fontWeight: "bold",
    margin: "2rem 0",
  },
  collections: {
    width: "100%",
    display: "grid",
    gap: "1.7rem",
    transition: "all 0.3s ease",
  },
  actions: {
    gap: "1rem",
  },
  action: {
    padding: "1.2rem 2.4rem !important",
    margin: "auto",
    borderRadius: "1.5rem",
  },
  // //////////////////////////

  AddCollection: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  addCollection__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  addCollection__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  addCollection__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  addCollection__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  addCollection__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  addCollection__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 0.1s ease-in-out",
    position: "relative",
  },
  addCollection__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  addCollection__bar: {
    marginBottom: "2rem",
    width: "100%",
  },
  addCollection__chromePicker: {
    position: "absolute",
    right: "100%",
    bottom: "100%",
  },
});

const useStyles2 = createUseStyles({
  collection: {
    padding: "2.2rem 2.5rem",
    borderRadius: "2rem",
    gap: "2rem",
    cursor: "pointer",
    backgroundColor: "var(--background-color-header)",
    transition: "all ease 0.3s",
  },
  collection__tag: {
    padding: "1.3rem",
    borderRadius: "1.5rem",
  },
  collection__name: {
    width: "fit-content",
    textAlign: "left",
    fontSize: "2rem",
    textTransform: "capitalize",
    display: "-webkit-box",

    overflow: "hidden",
    textOverflow: "ellipsis",
    wordBreak: "break-word",
  },
  collection__countTasks: {
    color: "#A1A1A6",
    fontSize: "1.6rem",
    width: "fit-content",
  },
});

const HandleData = () => {
  
};

export default function Home() {
  const classes = useStyles();

  // Var
  const [collections, setCollections] = useState([
    // {
    //   cId: 1,
    //   cName:
    //     "lorem ipsum dolor sit amet, consectetur adipsegf lorem ipsum dolor sit amet, consectetur adip",
    //   cColor: "yellow",
    // },
    // { cId: 2, cName: "abc", cColor: "red" },
    // { cId: 3, cName: "abc", cColor: "pink" },
    // { cId: 4, cName: "abc", cColor: "coral" },
    // { cId: 5, cName: "abc", cColor: "green" },
  ]);
  const [displayVal, setDisplayVal] = useState(false);

  useEffect(() => {
    axios
      .get(`https://mvn-task-manager.work/api/categories?limit=10&page=1`)
      .then((res) => {
        const items = res.data.items;
        console.log(items);
        
      })
      .catch((err) => {});
  }, []);

  // Function
  const toggleAddCollectionBar = (value) => (event) => {
    setDisplayVal(value);
  };

  // Component
  const ListCollection = () => {
    return (
      <div className="ListCollection">
        <div
          className="content"
          style={{
            minWidth: `${collections.length > 0 ? `70rem` : `40rem`}`,
          }}
        >
          <div
            className={clsx(
              classes.topBar,
              "w-100",
              collections.length > 0 ? "d-flex" : "",
              "align-items-center",
              "justify-content-between"
            )}
          >
            <div
              className={clsx(
                classes.title,
                collections.length == 0 ? "w-100" : ""
              )}
            >
              {collections.length > 0
                ? "Collections"
                : "You have no collections."}
            </div>

            <div
              className={clsx(
                classes.actions,
                collections.length > 0 ? "d-flex" : "w-100"
              )}
            >
              <button
                className={clsx(
                  classes.action,
                  collections.length == 0 ? "d-none" : "",
                  "searchCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
                )}
              >
                <ion-icon name="search-outline"></ion-icon>
              </button>

              <button
                className={clsx(
                  classes.action,
                  "addCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
                )}
                onClick={toggleAddCollectionBar(true)}
              >
                <ion-icon name="add-outline"></ion-icon>
                {collections.length > 0 ? "" : "Add Your First Collection"}
              </button>
            </div>
          </div>

          <div
            className={classes.collections}
            style={{
              gridTemplateColumns: `repeat(${
                collections.length > 0 ? "2" : "1"
              },1fr)`,
            }}
          >
            {collections &&
              collections.length > 0 &&
              collections.map((collection) => {
                return (
                  <Collection key={collection.cId} collection={collection} />
                );
              })}
          </div>
        </div>

        <AddCollection />
      </div>
    );
  };

  const AddCollection = () => {
    const classes = useStyles();

    // Var
    const [listColors, setListColors] = useState([
      "pink",
      "yellow",
      "orange",
      "red",
      "purple",
      "blue",
    ]);
    const [collectionColor, setCollectionColor] = useState("");
    const [collectionCustomColor, setCollectionCustomColor] = useState("");
    const [displayColorPicker, setDisplayColorPicker] = useState(false);

    // Function
    const setBgColor = (color) => (event) => {
      setCollectionColor(color);
      if (collectionCustomColor !== "") {
        setCollectionCustomColor("");
      }
    };
    const setCustomBgColor = (color) => (event) => {
      console.log(color);
      setCollectionCustomColor(color);
      if (collectionColor !== "") {
        setCollectionColor("");
      }
    };
    const cancelAddCollection = () => (event) => {
      setCollectionColor("");
      setCollectionCustomColor("");

      toggleAddCollectionBar(false)(event);
    };

    return (
      <div
        className={clsx(
          classes.AddCollection,
          "m-auto position-fixed",
          !displayVal && "d-none"
        )}
      >
        <div
          className={clsx(
            classes.addCollection__bg,
            "w-100 h-100 position-absolute"
          )}
          onClick={toggleAddCollectionBar(false)}
        ></div>

        <div
          className={clsx(
            classes.addCollection__container,
            "position-absolute"
          )}
        >
          <form method="post">
            <div className="addCollection__top d-flex w-100">
              <div className={clsx(classes.addCollection__caption, "mr-auto")}>
                add collection
              </div>
              <div
                className="addCollection__close-btn"
                onClick={toggleAddCollectionBar(false)}
              >
                <ion-icon name="close"></ion-icon>
              </div>
            </div>

            <div className={clsx(classes.addCollection__bar, "bar-name")}>
              <div className={clsx(classes.addCollection__title)}>name</div>
              <input
                type="text"
                placeholder="My Collection"
                className="btn--outline w-100 py-3"
                style={{ fontSize: "1.6rem" }}
              />
            </div>

            <div className={clsx(classes.addCollection__bar, "bar-color")}>
              <div className={clsx(classes.addCollection__title)}>color</div>
              <div className={clsx(classes.addCollection__colors, "w-100")}>
                {listColors.map(function (color) {
                  return (
                    <div
                      className={clsx(classes.addCollection__color)}
                      key={`${color}`}
                      style={{
                        border: `3px solid ${color}`,
                        backgroundColor:
                          collectionColor === color
                            ? `${color}`
                            : "transparent",
                      }}
                      onClick={setBgColor(color)}
                    ></div>
                  );
                })}
              </div>
            </div>

            <div
              className={clsx(classes.addCollection__bar, "bar-custom-color")}
            >
              <div className={clsx(classes.addCollection__title)}>
                custome color
              </div>
              <div
                className={clsx(classes.addCollection__color)}
                style={{
                  border: "3px solid #fff",
                  backgroundColor:
                    collectionCustomColor !== ""
                      ? `${collectionCustomColor}`
                      : "transparent",
                }}
              >
                {displayColorPicker ? (
                  <ChromePicker
                    color={
                      collectionCustomColor !== ""
                        ? `${collectionCustomColor}`
                        : "#000"
                    }
                    className={clsx(classes.addCollection__chromePicker)}
                    onChange={(updateColor) => setCustomBgColor(updateColor)}
                  />
                ) : (
                  ""
                )}
              </div>
            </div>

            <div
              className={clsx(classes.addCollection__buttons, "w-100 d-flex")}
            >
              <button
                className="addCollection__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
                style={{ backgroundColor: "#3D3C50" }}
                type="submit"
              >
                Create
              </button>
              <button
                type="reset"
                className="addCollection__button button btn--none-border btn--hover-bg-gray mb-0"
                onClick={cancelAddCollection()}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  return (
    <div className="Home">
      <ListCollection />
    </div>
  );
}
