// lib
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import axios from "axios";
import { toast } from "react-toastify";

import GetTasksFunc from "../GetTasksFunc";

const useStyles = createUseStyles({
  DeleteTask: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  deleteTask__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  deleteTask__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  deleteTask__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  deleteTask__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },

  deleteTask__buttons: {
    gap: "1rem",
    marginTop: "3rem",
    width: "100%",
    display: "flex",
  },
  deleteTask__bar: {
    marginBottom: "2rem",
    width: "100%",
  },

  deleteTask__detail: {
    color: "gray",
    fontSize: "1.6rem",
  },
});

const DeleteTask = ({ task, toggleFunc, displayVal, handleChangeData }) => {
  const classes = useStyles();

  const HandleSubmit = async (e) => {
    e.preventDefault();

    toggleFunc(false)();

    axios
      .delete(`api/tasks/${task.id}`)
      .then(async (res) => {
        toast.success("ðŸ¦„ Delete Task Successful!", {
          position: "top-center",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });

        const tasksData = await GetTasksFunc(4);
        handleChangeData(tasksData.data);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  };

  return (
    <div
      className={clsx(
        classes.DeleteTask,
        "m-auto position-fixed",
        !displayVal && "d-none"
      )}
    >
      <div
        className={clsx(
          classes.deleteTask__bg,
          "w-100 h-100 position-absolute"
        )}
        onClick={toggleFunc(false)}
      ></div>

      <div className={clsx(classes.deleteTask__container, "position-absolute")}>
        <form onSubmit={HandleSubmit}>
          <div className="deleteTask__top d-flex w-100">
            <div className={clsx(classes.deleteTask__caption, "mr-auto")}>
              delete Task
            </div>
            <div className="deleteTask__close-btn" onClick={toggleFunc(false)}>
              <ion-icon name="close"></ion-icon>
            </div>
          </div>

          <div className="deleteTask__details">
            <div className={clsx(classes.deleteTask__detail)}>
              Are you sure you want to delete?
            </div>
            <div className={clsx(classes.deleteTask__detail)}>
              This action cannot be undone.
            </div>
          </div>

          <div className={clsx(classes.deleteTask__buttons)}>
            <button
              className="deleteTask__button button btn--none-border ml-auto mb-0"
              style={{ backgroundColor: "red" }}
              type="submit"
            >
              Delete
            </button>
            <button
              type="reset"
              className="deleteTask__button button btn--none-border btn--hover-bg-gray mb-0"
              onClick={toggleFunc(false)}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DeleteTask;
