import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import axios from "axios";

import TaskBar from "./TaskBar";
import Task from "./Task";

import IsEmptyObject from "../../../../service/IsEmptyObject";

const useStyles = createUseStyles({
  tasks__tab: {
    marginTop: "3rem",
    width: "80%",
    maxWidth: "100rem",
  },
  tasks: {
    display: "grid",
    gap: "1.7rem",
    gridTemplateColumns: "repeat(auto-fit, minmax(20rem, 1fr))",
  },
});

export default function ViewTask() {
  const classes = useStyles();
  const [listTasks, setListTasks] = useState([]);
  const [status, setStatus] = useState("all");

  useEffect(() => {
    axios
      .get(`api/tasks?limit=10&page=1`)
      .then((res) => {
        console.log(res.data);
        setListTasks(res.data.items);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  }, []);

  const handleChangeStatus = (val) => {
    setStatus(val);
  };
  const handleChangeData = (tasksData) => {
    if (IsEmptyObject(tasksData)) {
      console.log("Khum co");
    } else {
      console.log("set data tasks");
      // setCollection(tasksData);
    }
  };

  return (
    <div className={clsx(classes.tasks__tab)}>
      <TaskBar
        status={status}
        count={listTasks.length}
        handleChangeData={handleChangeData}
        handleChangeStatus={handleChangeStatus}
      />

      <div className={clsx(classes.tasks)}>
        {listTasks.map((task) => {
          return (
            <Task
              key={task.id}
              task={task}
              handleChangeData={handleChangeData}
            />
          );
        })}
      </div>
    </div>
  );
}
