import React from "react";

export default function ViewTask({ listTasks, status }) {
  switch (status) {
    case "all":
      status = "All Task";
      break;
    case "inProcess":
      status = "InProcess Task";
      break;
    case "complete":
      status = "Complete Task";
      break;
      default : status
  }
  return (
    <div
      className="task__tab"
      style={{
        border: "1px solid red",
        marginTop: "3rem",
      }}
    >
      <div
        className="task__title"
        style={{
          fontSize: "2rem",
          textTransform: "capitalize",
          padding: "2rem",
        }}
      >
        {status} - {listTasks.length}
      </div>
      <div
        className="task__bar w-100 position-relative d-flex align-items-center"
        style={{
          padding: "0.3rem 1.3rem 1.3rem 1.3rem",
          borderTopLeftRadius: "1.3rem",
          borderTopRightRadius: "1.3rem",
          border: "3px solid var(--background-color-header)",
        }}
      >
        <input
          type="checkbox"
          className="task__button d-flex"
          style={{
            padding: "0.3rem",
            borderRadius: "0.7rem",
            // backgroundColor: `${collection.cColor}`,
          }}
        ></input>

        <div
          className="task__input w-100"
          style={{
            fontSize: "1.6rem",
            padding: "0.5rem 1.5rem",
          }}
        >
          {
            "sjdbnsdjv jdfkghfjb n euig hergigheruigheruig er g ueru g er ergu erigheru ierh gaserui ghergui "
          }
        </div>

        <div className="task__button position-relative">
          <ion-icon
            name="ellipsis-vertical"
            style={{
              fontSize: "2rem",
              cursor: "pointer",
            }}
          ></ion-icon>
          <div
            className="collection__actions position-absolute"
            style={{
              minWidth: "max-content",
              right: "0",
              borderRadius: "1.1rem",
              backgroundColor: "#1e1e29",
              zIndex: "2",
            }}
          >
            <div
              className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
              style={{
                width: "20rem",
              }}
            >
              edit task
            </div>
            <div
              className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
              style={{
                width: "20rem",
              }}
            >
              delete task
            </div>
          </div>
        </div>

        <div
          className="task__process w-100 position-absolute"
          style={{
            top: "100.5%",
            left: "0",
          }}
        >
          <div
            className="task__process-bar"
            style={{
              width: "0%",
              height: "2.5px",
              //   backgroundColor: `${collection.cColor}`,
            }}
          ></div>
        </div>
      </div>
    </div>
  );
}
